import React, { useState } from 'react';

function App() {
  const [issues, setIssues] = useState([
    {
      id: 1,
      title: 'Login not working',
      description: 'The login form throws an error.',
      status: 'Open'
    },
    {
      id: 2,
      title: 'Page not found error',
      description: '404 error on dashboard link.',
      status: 'Closed'
    }
  ]);

  const [newIssue, setNewIssue] = useState({
    title: '',
    description: '',
    status: 'Open'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewIssue({ ...newIssue, [name]: value });
  };

  const handleAddIssue = (e) => {
    e.preventDefault();
    const issueToAdd = {
      id: Date.now(),
      ...newIssue
    };
    setIssues([issueToAdd, ...issues]);
    setNewIssue({ title: '', description: '', status: 'Open' });
  };

  const handleDelete = (id) => {
    setIssues(issues.filter(issue => issue.id !== id));
  };

  return (
    <div>
      <h1>Issue Tracker</h1>

      <form onSubmit={handleAddIssue}>
        <input
          name="title"
          placeholder="Title"
          value={newIssue.title}
          onChange={handleChange}
          required
        />
        <br />
        <textarea
          name="description"
          placeholder="Description"
          value={newIssue.description}
          onChange={handleChange}
          required
        />
        <br />
        <select
          name="status"
          value={newIssue.status}
          onChange={handleChange}
        >
          <option value="Open">Open</option>
          <option value="Closed">Closed</option>
        </select>
        <br />
        <button type="submit">Add Issue</button>
      </form>

      <h2>Issues:</h2>
      {issues.map(issue => (
        <div key={issue.id}>
          <h3>{issue.title}</h3>
          <p>{issue.description}</p>
          <p>Status: {issue.status}</p>
          <button onClick={() => handleDelete(issue.id)}>Delete</button>
          <hr />
        </div>
      ))}
    </div>
  );
}

export default App;
