import React, { useState, useEffect } from "react";

const IssueList = () => {
  const [issues, setIssues] = useState(JSON.parse(localStorage.getItem("issues")) || []);
  const [filter, setFilter] = useState("All");
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  useEffect(() => { localStorage.setItem("issues", JSON.stringify(issues)); }, [issues]);

  const add = () => {
    if (title && desc) {
      setIssues([...issues, { id: Date.now(), title, description: desc, status: "Open" }]);
      setTitle(""); setDesc("");
    }
  };
  const toggle = id => setIssues(issues.map(i => i.id === id ? { ...i, status: i.status === "Open" ? "Closed" : "Open" } : i));
  const del = id => setIssues(issues.filter(i => i.id !== id));
  const shown = filter === "All" ? issues : issues.filter(i => i.status === filter);

  return (
    <div>
      <h1>Issue Tracker</h1>
      {["All", "Open", "Closed"].map(f => <button key={f} onClick={() => setFilter(f)}>{f}</button>)}
      <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
      <input placeholder="Description" value={desc} onChange={e => setDesc(e.target.value)} />
      <button onClick={add}>Add</button>
      <ul>
        {shown.map(i => (
          <li key={i.id}>
            {i.title} - {i.description} - {i.status}
            <button onClick={() => toggle(i.id)}>Toggle</button>
            <button onClick={() => del(i.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default function App() {
  return <IssueList />;
}
